function makeSound(s){switch(s){case"w":new Audio("assets/sounds/tom-1.mp3").play();break;case"a":new Audio("assets/sounds/tom-2.mp3").play();break;case"s":new Audio("assets/sounds/tom-3.mp3").play();break;case"d":new Audio("assets/sounds/tom-4.mp3").play();break;case"j":new Audio("assets/sounds/snare.mp3").play();break;case"k":new Audio("assets/sounds/crash.mp3").play();break;case"l":new Audio("assets/sounds/kick-bass.mp3").play();break;default:break}}var buttons=document.querySelectorAll(".drum");for(i=0;i<buttons.length;i++)buttons[i].addEventListener("click",(function(){let s=this.innerHTML;makeSound(s),buttonAnimation(s)}));function buttonAnimation(s){let e=document.querySelector("."+s);e&&(e.classList.add("pressed"),setTimeout((function(){e.classList.remove("pressed")}),100))}document.addEventListener("keydown",(function(s){makeSound(s.key),buttonAnimation(s.key)}));
